jvm.ColorScale=function(a,b,c,d){jvm.ColorScale.parentClass.apply(this,arguments)};jvm.inherits(jvm.ColorScale,jvm.NumericScale);jvm.ColorScale.prototype.setScale=function(b){var a;for(a=0;a<b.length;a++){this.scale[a]=jvm.ColorScale.rgbToArray(b[a])}};jvm.ColorScale.prototype.getValue=function(a){return jvm.ColorScale.numToRgb(jvm.ColorScale.parentClass.prototype.getValue.call(this,a))};jvm.ColorScale.arrayToRgb=function(a){var b="#",e,c;for(c=0;c<a.length;c++){e=a[c].toString(16);b+=e.length==1?"0"+e:e}return b};jvm.ColorScale.numToRgb=function(a){a=a.toString(16);while(a.length<6){a="0"+a}return"#"+a};jvm.ColorScale.rgbToArray=function(a){a=a.substr(1);return[parseInt(a.substr(0,2),16),parseInt(a.substr(2,2),16),parseInt(a.substr(4,2),16)]};