(11,	'thumb(url,size)',	0,	'Hiển thị một ảnh thu nhỏ của đường dẫn url, với kích thước size ví dụ 50x50',	0),
(12,	'cur_url',	0,	'Lấy đường dẫn hiện tại',	0),
(13,	'ajax(dữ liều truyền,trả về)',	0,	'Truyển và chuyển dữ liệu vào tệp xử lý ajax.php đối với mỗi cms tương ứng',	0);
INSERT INTO `work_help` (`id`, `title`, `contain`, `father_id`, `user_create`, `orders`) VALUES
(1,	'Xuất bản dữ liệu',	'Đây là trình quản lý dữ liệu nháp chờ xuất bản mà quản trị viên đã lọc dữ liệu hợp lý từ người dùng cho editor xuất bản. Khi nhận được nhiệm vụ xuất bản các dữ liệu người dùng thì editor hãy vào trang này để làm việc.\r\n<br/>\r\n<br/>\r\n<strong>Có hai cách để vào chức năng xuất bản dữ liệu nháp:</strong><br/>\r\n<strong>Cách một:</strong><br/>\r\nBạn có thể vào chức năng này ở (CMS - Virtual lover) theo đường dẫn\r\n<a href=\"http://carrotstore.com/app_my_girl_handling.php?func=draft_brain\">http://carrotstore.com/app_my_girl_handling.php?func=draft_brain</a>\r\n<br/>\r\n<strong>Cách hai:</strong><br/>\r\nHoặc từ menu hệ thống (CMS - Virtual lover) bấm vào chức năng <strong>Công cụ</strong> và chọn <strong>Xuất bản các bản nháp</strong>\r\n<br/>\r\n<br/>\r\nKhi bản dữ liệu trống, không có dữ liệu thì Editor nên thông báo, nhắn tin liên hệ với quản trị viên để đẩy dữ liệu vào cho bạn xuất bản.\r\n',	2,	4,	0),
(2,	'Các công việc cơ bản của Editor',	'<p>C&aacute;c c&ocirc;ng việc cơ bản của Editor (Người ph&aacute;t triển nội dung) xoay quanh việc tương t&aacute;c với dữ liệu hệ thống&nbsp;như: th&ecirc;m c&aacute;c đối tượng tr&ograve; chuyện, c&acirc;u thoại, ảnh nền, radio v&agrave; ch&acirc;m ng&ocirc;n tục ngữ.<br />Tận dụng khả năng s&aacute;ng tạo nội dung của bản th&acirc;n để đưa v&agrave;o nghiệp vụ khi cần.</p>',	0,	4,	2),
(3,	'Thêm châm ngôn',	'Ở CMS có những điểm cần chú ý cho những công việc liên quan đến châm ngôn đó là nút thêm châm ngôn và nút vào danh sách châm ngôn\r\n<br/>\r\n\r\nKhi nhận được nhiệm vụ thêm châm ngôn vào các nước nào đó, Editor ở trang chính CMS, hãy bấm vào biểu tượng (+Quote) để thêm châm ngôn vào hệ thống với nước tương ứng.<br/>\r\n\r\nSau đó sẽ xuất hiện một trang để bạn nhập dữ liệu vào, bạn chỉ cần quan tâm đến trường dữ liệu (chat), còn những trường khác CMS đã tự động thiết lập các thuộc tính để phù hợp với chức năng châm ngôn! đặc biệt là hai trường dữ liệu (text,effect) không được thay đổi!\r\n<br/>\r\n\r\nCác bước còn lại: thực hiện thay đổi các trường phù hợp như bước 2 của việc Duyệt dữ liệu (chú ý tạo âm thanh, biểu tượng và hành động phù hợp nếu câu châm ngôn dễ miêu tả. Và đặc biệt là không được thay đổi hai trường dữ liệu (text,effect))',	2,	4,	0),
(4,	'Báo cáo tác vụ',	'Sau khi thêm mới hoặc cập nhật một dữ liệu, CMS sẽ tự động tạo ra một nút \"Thêm vào bàn làm việc\" để Editor báo cáo vào trang làm việc (không chỉ trang cms - Virtual có mà tất cả trình quản lý dữ liệu khác của hệ thống Carrot đều có nút này)',	0,	4,	9),
(5,	'Nội Quy',	'<ul>\r\n\r\n<li>\r\n<strong>Bảo mật:</strong> Đối với các thành viên Editor,Coder,Designer,Tester không được chia sẻ những nghiệp vụ và những thông tin về cms (trang quản lý dữ liệu của ứng dụng virtual lover) cho người khác không liên quan đến công việc xem.\r\n</li>\r\n    \r\n<li>\r\n<strong>Đúng nghiệp vụ:</strong> không làm những tác vụ khác không liên quan đến công việc của mình !\r\n</li>\r\n\r\n    \r\n<li>\r\n<strong>Sự cố:</strong> Trong quá trình làm việc, nếu thấy có những thay đổi bất thường, cần báo cho quản trị viên biết,không tự ý tìm cách khắc phục.\r\n</li>\r\n\r\n<li>\r\n<strong>Tác phong làm việc:</strong>Nếu mệt hoặc buồn ngủ có thể rời chỗ làm để ngủ và nghỉ ngơi (không được ngáp hay có những thái độ tỏ ra mệt mỏi gây ảnh hưởng đến những thành viên khác trong nhóm).\r\n</li>\r\n\r\n<li>\r\n<strong>Thời gian làm việc:</strong>Có thể đi trễ hoặc không đúng giờ mà không cần xin phép trước miễn sao hoàn thành nhiệm vụ trong ngày.\r\n</li>\r\n\r\n</ul>',	0,	4,	10),
(6,	'Khai báo việc vừa làm',	'Sau khi bấm vào nút <strong>\"Thêm vào bàn làm việc\",</strong> hệ thống sẽ chuyển sang trang làm việc (work) và tự động điền tham số. Bạn chỉ cần bấm vào nút hoàn tất để hoàn thành việc khai báo công việc của mình. Trang làm việc sẽ ghi lại lịch sử làm việc của bạn để thuận tiện cho việc sửa chữa, quản lý và đánh giá hiệu năng của Editor.\r\nĐối với những tác vụ đặc biệt, quản trị viên sẽ hướng dẫn Editor chi tiết về việc báo cáo.',	4,	4,	0),
(7,	'Các phần mềm và trang web nên cài đặt để làm việc',	'Dưới đây là những phần mềm tham khảo, Editor không cần phải sử dụng một trong số chúng. Trong quá trình làm việc, có những tác vụ cần đến những công cụ này thì quản trị viên sẽ hướng dẫn Editor sử dụng một trong số đó.\r\n<ul>\r\n    <li>\r\n    Các phần mềm cần thiết\r\n        <ul>\r\n        <li><strong>NHMTTS SAPI5 Application</strong> - phần mềm tạo giọng đọc nam tiếng Việt</li>\r\n        <li><strong>Balabolka</strong> - phần mềm chuyển đổi văn bản các ngôn ngữ khác nhau thành file âm thanh</li>\r\n        <li><strong>Freemake Audio Converter</strong> - phần mềm chuyển đội các định dạng file âm thanh thành định dạng mp3</li>\r\n        <li><strong>Chrome Remote Desktop</strong> - phần mềm để quản trị viên (admin) theo dõi và hỗ trợ người phát triển nội dụng (editor) làm việc</li>\r\n        </ul>\r\n    </li>\r\n    \r\n    <li>\r\n    Các trang web hỗ trợ làm việc tạo file âm thanh\r\n        <ul>\r\n        <li><strong><a href=\"http://www.fromtexttospeech.com/\" target=\"_blank\">http://www.fromtexttospeech.com</a></strong> - Trang web tạo giọng nam để chèn vào câu trò chuyện</li>\r\n        <li><strong><a href=\"http://www.text2speech.org/\" target=\"_blank\">http://www.text2speech.org</a></strong> - Trang web tạo giọng nam để chèn vào câu trò chuyện</li>\r\n        <li><strong><a href=\"https://www.ofoct.com/merge-mp3-files\" target=\"_blank\">https://www.ofoct.com/merge-mp3-files</a></strong> - Trang nối file mp3</li>\r\n        <li><strong><a href=\"http://kobaspeech.com/en/download-voices\" target=\"_blank\">http://kobaspeech.com/en/download-voices</a></strong> - Một số phần mềm giọng các nước</li>\r\n        <li><strong><a href=\"http://www.cross-plus-a.com/balabolka.htm\" target=\"_blank\">http://www.cross-plus-a.com/balabolka.htm</a></strong>  - trang tải phần mềm tạo giọng đọc</li>\r\n        <li><strong><a href=\"http://www.nuance.com/landing-pages/playground/Vocalizer_Demo2/vocaLizer_modal.html?demo=false\" target=\"_blank\">vocaLizer_modal.html</a></strong> - web koba - trang tải phần mềm tạo giọng đọc</li>\r\n        <li><strong><a href=\"http://www.radioonline.vn\" target=\"_blank\">http://www.radioonline.vn</a></strong>  - trang web lấy bộ đệm trực tuyến radio</li>\r\n        <li><strong><a href=\"https://drive.google.com/file/d/0B-dFJ0GRGRpuMU53bEdhVTBPTG8/view\" target=\"_blank\">https://drive.google.com/file/d/0B-dFJ0GRGRpuMU53bEdhVTBPTG8/view</a></strong> Phần mềm chuyển giọng nữ thành nam (thai) (Password: tinvoker)</li>\r\n        <li><strong><a href=\"https://www.text2speech.org/\" target=\"_blank\">https://www.text2speech.org/</a></strong>Trang web lấy Giọng nam ấn độ</li>\r\n        <li><strong><a href=\"https://ttsmp3.com/text-to-speech/\" target=\"_blank\">https://ttsmp3.com/text-to-speech</a></strong> Giọng Nam - nga(ru), giọng nữ - hàn(ko), giọng nam - Nhật bản (ja)</li>\r\n        <li><strong><a href=\"https://texttospeech.io/\" target=\"_blank\">https://texttospeech.io/</a></strong> Giọng nam - Ả rập</li>\r\n\r\n<li>\r\n<a href=\"https://usersdrive.com/8x6old112e8g.html\">Phần mềm chuyển link ytb thành mp3 https://usersdrive.com/8x6old112e8g.html</a>\r\n</li>\r\n        </ul>\r\n    </li>\r\n    \r\n    <li>\r\n    Các tài khoản cần thiết để làm việc\r\n        <ul>\r\n        <li><strong>Gmail</strong> - để dùng phần mềm Chrome Remote Desktop và trả lời đánh giá của người dùng</li>\r\n        <li><strong>Tài khoản ngân hàng nội địa</strong> - đối với những thành viên muốn thanh toán trực tuyến không cần tiền mặt</li>\r\n        </ul>\r\n    </li>\r\n</ul>',	0,	2,	0),
(8,	'CMS - Virtual lover',	'cms - Virtual lover là hệ thống quản lý dữ liệu của ứng dụng Virtual lover (Hoat động trên 3 phiên bản Virtual lover, Virtual lover 2, Virtual lover 3D) \r\n<br/>\r\n\r\nĐịa chỉ truy cập cms (http://carrotstore.com/vl) được bảo mật bởi tài khoản làm việc (Nếu bạn truy cập vào hệ thống mà xuất hiện biểu mẫu đăng nhập thì bạn hãy nhập tài khoản làm việc của bạn) \r\n<br/>\r\n\r\nCms gồm có hai đối tượng chính là <strong>msg</strong> và <strong>chat</strong> để người thiết kế nội dung và kiểm duyệt (editor) quan tâm :\r\n\r\n',	0,	4,	1),
(9,	'msg - câu thoại',	'<p>L&agrave; c&aacute;c c&acirc;u thoại của nh&acirc;n vật hiển thị như:&nbsp;&nbsp;ch&agrave;o khi mở ứng dụng, th&ocirc;ng b&aacute;o v&agrave; bắt chuyện<br /><br />(Đ&acirc;y l&agrave; những dữ liệu nh&acirc;n vật chủ động tương t&aacute;c với người d&ugrave;ng)</p>',	8,	4,	0),
(10,	'chat - câu trò chuyện',	'Là những câu trò chuyện người dùng chủ động tương tác với nhân vật (khi người dùng nhập nội dung trò chuyện vào và nhân vật trả lời )\r\n<br/>\r\n<br/>\r\n\r\n<strong>Gồm có 3 loại câu trò chuyện:</strong>\r\n<ul>\r\n<li>\r\n<i class=\"fas fa-circle\" aria-hidden=\"true\"></i>\r\n<strong>câu trò chuyện toàn cục</strong> : là câu trò chuyện người dùng có thể tương tác bất cứ lúc nào\r\n</li>\r\n                \r\n<li> \r\n<i class=\"far fa-dot-circle\"></i> <strong>câu trò chuyện ghi lại</strong>: là những câu hỏi của người dùng có cùng câu trả lời ( giúp editor đỡ phải viết lại)</li>\r\n                <li> <i class=\"far fa-circle\" aria-hidden=\"true\"></i> <strong>câu trò chuyện đơn</strong>: câu trò chuyện chỉ trả lời cho một câu trò chuyện toàn cục</li>\r\n            </ul>',	8,	4,	0),
(12,	'Cách thêm câu trò chuyện (chat)',	'<p>Ở m&agrave;n h&igrave;nh ch&iacute;nh CMS, bạn sẽ thấy n&uacute;t (+ chat) ở mỗi quốc gia. Bạn h&atilde;y bấm v&agrave;o đ&oacute; để th&ecirc;m c&acirc;u tr&ograve; chuyện với mỗi quốc gia tương ứng.</p><p>C&ocirc;ng việc n&agrave;y gồm hai bước ch&iacute;nh:</p>',	10,	4,	0),
(13,	'Bước 1',	'Bấm nút thêm (ở danh sách chờ xuất bản)',	1,	4,	0),
(14,	'CMS - Kinh Thánh',	'Đây là trình quản lý dữ liệu cms kinh thánh để các Editor quản lý nội dung tương ứng với mỗi nước<br/>\r\n\r\n-Mỗi nước đều có hai loại chủ đề sách để quan tâm đó là:<br/>\r\n<strong>Tân ước:</strong><br/>\r\n<strong>Cựu ước:</strong><br/>',	0,	5,	3),
(15,	'Cách thêm sách',	'Ở Trang chính của CMS luôn có hai loại chủ đề sách để thêm đó là sách Tân ước và Cựu ước. Mỗi mục đều có nút thêm tương ứng để Editor quản lý các loại sách và thêm mới',	14,	4,	0),
(16,	'Thêm truyện (truyện cười,truyện cổ tích,vv..)',	'<p>Ở mỗi quốc gia đều c&oacute; một n&uacute;t th&ecirc;m truyện ngắn tương ứng. (Tương tự với ch&acirc;m ng&ocirc;n)</p><ul><li><strong>Những lưu &yacute; khi th&ecirc;m truyện ngắn</strong></li><li>Sau khi bấm v&agrave;o n&uacute;t &quot;story&quot; sẽ hiện ra trang th&ecirc;m dữ liệu. Editor cần th&ecirc;m nội dung tr&ograve; chuyện v&agrave;o mục &quot;chat&quot;</li><li>Chọn biểu tượng li&ecirc;n quan tới nội dung của truyện, nếu kh&ocirc;ng c&oacute; c&aacute;c biểu tượng li&ecirc;n quan th&igrave; c&oacute; thể chọn biểu tượng ở mục &quot;chat&quot; hoặc &quot;emoji&quot;</li><li>H&agrave;nh động v&agrave; m&ocirc; tả khu&ocirc;n mặt thể hiện khi đọc tuyện (tương tự như th&ecirc;m tr&ograve; chuyện)</li><li>Hiển thị v&agrave; đ&aacute;nh gi&aacute; giới hạn mức độ th&ocirc; tục li&ecirc;n quan tới nội dung truyện (tương tự như th&ecirc;m tr&ograve; chuyện)</li><li>Hầu hết khi tạo &acirc;m thanh (giọng đọc), thường truyện sẽ c&oacute; nội dung d&agrave;i hơn n&ecirc;n APi voice google đọc kh&ocirc;ng được, Editor cần tạo &acirc;m thanh với &quot;C&ocirc;ng cụ tạo giọng d&agrave;i&quot;. Sử dụng c&ocirc;ng cụ n&agrave;y cần kiểm tra xem từng h&agrave;ng khi tạo c&oacute; bị thừa hoặc hiếu k&iacute; tự hay kh&ocirc;ng để bổ sung cho đầy đủ một c&acirc;u.</li></ul>',	2,	4,	0),
(17,	'Bảng điều khiển Ch Play',	'Là nơi quản lý các ứng dụng Anroid của Carrot. Mỗi biên tập viên (Editor) sẽ được chỉ định chỉnh sửa nội dụng theo yêu cầu của quản trị viên (Admin) .Khi làm những tác vụ liên quan đến mục này thì biên tập viên được admin mời vào chỉnh sửa nội dung hiển thị trên chplay theo email của mỗi người và mỗi người có thể thấy danh sách ứng dụng riêng khi làm việc.',	0,	4,	4),
(18,	'Thêm nội dung tính năng mới',	'Thêm nội dung tính năng mới nhằm thông báo cho người dùng biết các tính năng mới trong bản cập nhật này.<br/>\r\n\r\nĐể thay đổi nội dung này cần thực hiện lần lượt các bước hướng dẫn tiếp theo ở bên dưới:\r\n\r\n',	17,	4,	0),
(19,	'Cách Truy cập chỉnh sửa nội dung phát hành',	'Ở danh sách chính hiển thị tất cả các ứng dụng. Bấm vào một ứng dụng cần chỉnh sửa sẽ hiện ra một bản điều khiển như trong hình\r\n(bấm vào hình để quan sát rõ hơn)\r\n<br/>\r\nB1:bấm vào mục \"Release management\"<br/>\r\nB2: vào phần  \"App releases\"<br/>\r\nb3: chọn Manage<br/>\r\n',	18,	4,	0),
(20,	'Cách Thay đổi nội dung tính năng mới',	'Bấm vào \"Edit release notes\" để xuất hiện hộp thoại thay đổi nội dung cập nhật ở mỗi nước<br/>\r\n\r\nTrong hộp thoại nhỏ hiện ra, Editor cần dịch thuật và thay đổi nội dung theo từng quốc gia ở các cặp khóa. Ví dụ <en-us><b>Nội dung dịch thuật</b></en-us><br/><br/>\r\n\r\nKhi dịch và bổ sung xong một nước (cặp khóa quốc gia có trong cửa sổ) thì editor khai báo ở chỗ làm việc với id do admin cấp và loại là \"chplay\"<br/>\r\n\r\n\r\nSau khi hoàn tất bấm save để lưu lại<br/>\r\n\r\nNếu lưu lại không được thì Editor có thể xóa những bớt những dòng không cần thiết vì nội dụng cập nhật bị giới hạn số lượng chữ<br/>\r\n\r\n\r\n',	18,	4,	0),
(21,	'Thay đổi thông tin và ảnh chụp ứng dụng',	'Để thay đổi nội dung và ảnh chụp ứng dụng từ danh sách ứng dụng, truy cập vào ứng dụng muốn chỉnh sửa<br/>\r\nbước 1:chọn \"Store presence\"<br/>\r\nbước 2:chọn \"Store listing\"<br/>\r\nbước 3:chọn ngôn ngữ cần thay đổi nội dung từ danh sách (như trong hình, bấm vào hình để quan sát rõ hơn)\r\n\r\nKhi nhận những tác vụ liên quan này, cần đọc kĩ nội dung nghiệp vụ mà quản trị viên giao, tránh sai sót.',	17,	4,	0),
(22,	'Samsung Galaxy Store',	'Là nơi quản lý các ứng dụng trên Samsung Store, truy cập vào liên kết dưới để vào danh sách:<br/>\r\n<a href=\"https://seller.samsungapps.com/content/common/summaryContentList.as\">https://seller.samsungapps.com/content/common/summaryContentList.as</a><br/>\r\n\r\nTài khoản đăng nhập:<br/>\r\nid:tranthienthanh93@gmail.com<br/>\r\npassword:28091993Kurot\r\n<br/>\r\n<br/>\r\nBấm vào nút update màu vàng hoặc nút update ở mỗi ứng dụng để thay đổi thông tin khi được quản trị viên chỉ định (bấm vào tất cả các hình để xem chi tiết rõ ràng hơn)',	0,	4,	5),
(23,	'Cách truy cập và tạo danh sách ngôn ngữ',	'Sau khi truy cập vào chức năng cập nhật (update) của mỗi ứng dụng tương ứng thì thực hiện các bước như trong hình để làm hiện danh sách\r\ncác ngôn ngữ<br/>\r\n\r\nBước 1:Bấm vào nút Mode chọn (\"Advanced\") để vào chế độ nâng cao<br/>\r\nBước 2:Bấm vào nút \"Add\" để thêm ngôn ngữ<br/>\r\nBước 3:Chọn các nước có trong hệ thống<br/>\r\nBước 4:Save lại danh sách<br/>\r\n<br/>\r\n\r\nXem các bước tiếp theo để biết cách nhập thông tin cho ứng dụng.',	22,	4,	0),
(24,	'Cập nhật và thêm mới thông tin',	'Sau khi thêm mới các danh sách ngôn ngữ cho ứng dụng, thực hiện các bước sau ở mỗi nước để cập nhật hoặc thêm mới nội dung<br/>\r\n<br/>\r\nBước 1: Bấm vào nút \"Select language\" để hiện danh sách đã thêm<br/>\r\nBước 2: chọn một nước trong danh sách<br/>\r\nBướ 3,4,5:Dịch thuật các tiêu đề và mô tả từ nước cho trước sang nước này<br/>\r\nBước 6: bấm nút \"save\" để lưu lại thông tin<br/>\r\n<br/>\r\n\r\nkhi đã làm xong một nước, biên tập viên cần khai báo công việc với \"id\" và \"loại\" mà quản trị cấp, rồi thực hiện tương tự các nước tiếp theo<br/><br/>\r\n\r\n(Tác vụ thay đổi ảnh chụp ứng dụng cũng làm tương tự. Khi được quản trị viên yêu cầu, kéo xuống danh sách ở màn hình này có khu vực để thay đổi và thêm ảnh chụp)\r\n\r\n\r\n',	22,	4,	0),
(25,	'Carrotstore Music',	'Là kho nhạc trực tuyến, mỗi quốc gia đều có danh sách nhạc riêng. Khi chọn thay đổi ngôn ngữ ở danh sách chọn thì các bài hát sẽ thay đổi theo mỗi quốc gia tương ứng . Các biên tập viên cần lưu ý khi làm các tác vụ liên quan đến kho nhạc này.\r\n<br/>\r\n<br/>\r\n\r\nĐịa chỉ try cập:<a href=\"http://carrotstore.com/music\" target=\"_blank\">http://carrotstore.com/music</a>\r\n<br/>\r\n\r\nTrước khi làm các tác vụ này, các editor cần đăng nhập tài khoản làm việc ở Admin carrotstore liên kết :<a href=\"http://carrotstore.com/admin/\" target=\"_blank\" >http://carrotstore.com/admin/</a> để hiện thị các chức năng đầy đủ cho người biên tập.',	0,	4,	7),
(26,	'Các lỗi thường gặp',	'Theo giời gian, các bài hát trên Carrotstore Music sẽ bị mất đi ảnh đại diện do liên kết Youtube bị mất.\r\n<br/>\r\nCác biên tập viên cần tìm các lỗi như trong hình để thực hiện việc chỉnh sửa bài hát<br/>\r\n<br/>\r\nCác lỗi thường gặp:<br/>\r\n-Thiếu ảnh đại diện âm nhạc<br/>\r\n-Thiếu lời bài hát<br/>\r\n\r\n<br/>\r\nCác Editor cần cuộn thanh dọc của trình duyệt xuống dưới để trang web nạp dữ liệu mới và để tìm lỗi\r\n',	25,	4,	0),
(27,	'Truy cập vào trang chỉnh sửa',	'<p>Khi đ&atilde; t&igrave;m thấy lỗi, bấm v&agrave;o b&agrave;i h&aacute;t n&uacute;t &quot;Xem chi tiết&quot; hoặc bấm v&agrave;o ti&ecirc;u đề trang nhạc tương ứng n&agrave;y!<br />Bấm v&agrave;o n&uacute;t &quot;chỉnh sửa b&agrave;i h&aacute;t&quot; để cập nhật lại th&ocirc;ng tin cho b&agrave;i h&aacute;t. N&uacute;t n&agrave;y chỉ hiện ra khi c&aacute;c Editor đ&atilde; đăng nhập v&agrave;o hệ thống</p><p><strong>Đăng nhập hệ thống bằng hai c&aacute;ch:</strong></p><p><strong>C&aacute;ch 1</strong>: Truy cập v&agrave;o đường dẫn&nbsp;<a href=\"https://carrotstore.com/admin/\">https://carrotstore.com/admin/</a>&nbsp;rồi nhập th&ocirc;ng tin đăng nhập l&agrave;m việc của bạn.</p><p><strong>C&aacute;ch 2</strong>:&nbsp; Click chuột phải v&agrave;o n&uacute;t đăng nhập ở thanh menu của carrotstore rồi nhập th&ocirc;ng tin đăng nhập l&agrave;m việc của bạn.</p>',	26,	4,	0),
(28,	'Cập nhật thông tin cho bài hát',	'Kéo xuống phần âm nhạc ở phía dưới trang dữ liệu để cập nhật lời bài hát và ảnh đại diện.\r\n<br/>\r\n<br/>\r\nĐể tìm ảnh đại diện bài hát, bấm vào nút \"Tìm video trên youtube\",<br/> Tìm được video phù hợp tương ứng thì hãy sao chép và cập nhật liên kết bằng nút \"cập nhật liên kết video\".\r\n<br/>\r\n<br/>\r\nNếu không tìm được liên kết video nào phù hợp, không có ảnh đại diện thì Editor cần thiết kế ảnh đại diện mới cho bài hát rồi tải lên ảnh đại diện ở nút kế bên.\r\n\r\n',	26,	4,	0),
(29,	'Tìm kiếm ảnh cho bài hát',	'Nếu liên kết youtube không có ảnh thì Editor tìm ảnh bằng công cụ google với tên bài hát và ca sĩ thể hiện, rồi lưu ảnh lại và tải lên cho bài hát (nhưng phải chỉnh lại kích thước 640x480 trước khi tải lên). Hướng dẫn tiếp theo sẽ giới thiệu một phần mềm <b>(RealWorkd Paint)</b> chỉnh sửa nhẹ và tiện lợi cho việc xử lý ảnh.',	26,	4,	0),
(30,	'Chỉnh sửa kích thước ảnh bằng RealWorkd Paint',	'Liên kết tải phần mềm <a href=\"http://www.rw-designer.com/image-editor\" target=\"_blank\">http://www.rw-designer.com/image-editor</a>\r\n<br>\r\n\r\nMở tệp tin ảnh cần chỉnh sửa và bấm tập lệnh bàn phím <b>Ctrl + E</b> để xuất hiện chức năng chỉnh sửa kích thước. Nhập kích thước vào \"Specify size\" 640 và 480 để thay đổi',	26,	4,	0),
(31,	'Cách báo cáo tác vụ sửa nhạc',	'Sau khi thực hiện xong các việc chỉnh sửa dữ liệu nhạc, các editor cần khai báo bằng tay vào chỗ làm việc.<br/>\r\n\r\nĐể ý trên thanh địa chỉ trình duyệt có hai tham số id và lang.<br/>\r\nCopy id, và xác định đúng ngôn ngữ để thêm vào chỗ làm việc.',	26,	4,	0),
(32,	'AppGallery Huawei',	'AppGallery là kho ứng dụng của Huawei phổ biến ở thị trường ứng dụng Trung Quốc. Các biên tập viên khi làm việc ở store này cần đăng nhập bằng tài khỏan:<br/>\r\n\r\nUsername: <b>tranthienthanh93@gmail.com</b><br/>\r\nPassword: <b>28091993Kurots</b><br/>\r\n\r\n\r\n<br/>\r\nĐường dẫn<br/>\r\n<a href=\"https://developer.huawei.com/consumer/en/service/josp/agc/index.html\">https://developer.huawei.com/consumer/en/service/josp/agc/index.html</a>\r\n<br/>\r\n\r\nKhi đăng nhập thành công sẽ xuất hiện một trang hiển thị các dịch vụ mà store cung cấp, bấm vào mục <b>\"My apps\"</b> để quản lý các ứng dụng',	0,	4,	6),
(33,	'My app - huawei',	'Chọn ứng dụng (bằng cách bấm vào tên) để chỉnh sửa thông tin khi được quản trị viên giao việc tương ứng.',	32,	4,	0),
(34,	'App information - huawei',	'Khi đã vào trang chỉnh sửa thông tin ứng dụng, các biên tập viên cần thực hiện các bước <br/>\r\n<br/>\r\n<b>Bước 1:</b> bấm vào phần <b>App information</b><br/>\r\n<b>Bước 2:</b> Tìm đến phần <b>Localization </b><br/>\r\n<b>Bước 3:</b> Bấm vào nút <b>Add</b> để thêm các ngôn ngữ khác vào ứng dụng </b><br/>\r\n<b>Bước 4:</b> Chọn các ngôn ngữ có trong hệ thống để thêm vào ứng dụng<br/>\r\n<b>Bước 5:</b> Thanh đổi tên và mô tả (tính năng mới và ảnh chụp ứng dụng nếu có) tương ứng với từ quốc gia ở thanh sổ xuống mục <b>Language</b><br/>\r\n<b>Bước 6:</b> Bấm nút <b>Save</b> để lưu lại<br/>\r\n<br/>\r\n<br/>\r\n<i>Sau khi hoàn tất thêm thông tin, các biên tập viên cần báo cáo làm việc để tính công với id mà quản trị viên cấp, loại là \"AppGallery - huawei\" và thực hiện tiếp các bước 3,4,5,6 để thêm các ngôn ngữ khác</i>\r\n',	32,	4,	0),
(35,	'Cách thêm thông số File âm thanh nhạc',	'<p>Để th&ecirc;m c&aacute;c th&ocirc;ng số &acirc;m nhạc bị thiếu cho c&aacute;c tệp tin &acirc;m nhạc đ&atilde; tải l&ecirc;n m&aacute;y chủ, c&aacute;c bi&ecirc;n tập vi&ecirc;n h&atilde;y&nbsp;truy cập v&agrave;o trang web<a target=\"_blank\" href=\"http://carrot.sytes.net/\"> http://carrot.sytes.net/</a> rồi chọn v&agrave;o menu &quot;c&ocirc;ng cụ&quot;, nhấn v&agrave;o mục &quot;Kiểm tra c&aacute;c tệp tin &acirc;m nhạc thiếu th&ocirc;ng số&quot; Hoặc truy cập nhanh v&agrave;o địa chỉ <a target=\"_blank\" href=\"http://carrot.sytes.net/tool\">http://carrot.sytes.net/tool</a></p>',	0,	4,	8),
(36,	'Bước 1:Chọn tệp để bổ sung thông tin',	'<p>Sau khi truy cập li&ecirc;n kết, hệ thống sẽ hiển thị ra tất cả c&aacute;c tệp tin thiếu th&ocirc;ng số vật l&iacute; v&agrave; th&ocirc;ng số của m&aacute;y chủ. Bạn h&atilde;y lần lượt cập nhật từng tệp tin ở danh s&aacute;ch n&agrave;y&nbsp;</p>',	35,	4,	0),
(37,	'Bước 2: Bổ sung thông tin từng mục cho tệp',	'<p>Thực hiện c&aacute;c bước nhỏ trong giai đoạn của bước 2 n&agrave;y như trong h&igrave;nh ảnh<br /><strong>Bước 1: </strong>Bấm v&agrave;o n&uacute;t <strong>Lấy</strong> <strong>th&ocirc;ng tin từ server ch&iacute;nh&nbsp;</strong>&nbsp;<br /><strong>Bước 2</strong>: Đợi khi n&agrave;o ảnh đại diện được hiển&nbsp;thị th&igrave; tiếp tục&nbsp; bấm v&agrave;o n&uacute;t&nbsp;&nbsp;<strong>Nhập th&ocirc;ng tin v&agrave;o Th&ocirc;ng số &acirc;m nhạc</strong><strong>.&nbsp; C</strong>&aacute;c trường dữ liệu sẽ tự động được điền v&agrave;o c&aacute;c trường th&ocirc;ng tin.&nbsp;<br /><strong>Bước 3</strong>: Kiểm tra th&ocirc;ng tin c&aacute;c trường dữ liệu c&oacute; đ&uacute;ng kh&ocirc;ng, bằng c&aacute;ch bấm v&agrave;o n&uacute;t&nbsp;<strong>T&igrave;m kiếm th&ocirc;ng tin tr&ecirc;n google</strong> để đối chiếu. Nếu dữ liệu c&aacute;c trường sai th&igrave; sửa lại v&agrave; bổ sung khi bị thiếu.<br /><strong>Bước 4</strong>:<br />- Nhập cẩn thận, tr&aacute;nh sai s&oacute;t khi nhập dữ liệu .<br />- Title: c&oacute; thể gồm t&ecirc;n b&agrave;i h&aacute;t v&agrave; ca sĩ thể hiện.<br />- Album: Nếu t&igrave;m kh&ocirc;ng c&oacute; t&ecirc;n album th&igrave; để &nbsp;&quot;<em>carrotstore.com&quot;</em><br /><em>- Trường (Thể loại - Genre): nhập v&agrave;o phải l&agrave; dữ liệu ng&ocirc;n ngữ tiếng anh hoặc tr&ugrave;ng với ng&ocirc;n ngữ lời b&agrave;i h&aacute;t (Editor n&ecirc;n đăng xuất t&agrave;i khoản google ra khỏi tr&igrave;nh duyệt để kh&ocirc;ng hiển thị tiếng Việt khi t&igrave;m kiếm th&ocirc;ng tin).</em><br />- Trường (Năm - Year): nếu t&igrave;m kh&ocirc;ng c&oacute; th&ocirc;ng tin th&igrave; h&atilde;y bấm v&agrave;o ảnh đại điện để lấy năm dựa v&agrave;o năm video được đăng tr&ecirc;n youtube.<br />- C&oacute; thể bỏ qua c&aacute;c trường dữ liệu khi t&igrave;m kiếm&nbsp;kh&ocirc;ng c&oacute;.</p><p><strong>Bước 5: B</strong>ấm v&agrave;o n&uacute;t cập nhật để ho&agrave;n tất việc th&ecirc;m th&ocirc;ng số File &acirc;m nhạc</p>',	35,	4,	0),
(38,	'Bước 3 :Cập nhật thông tin bài hát lên máy chủ chính và báo cáo công việc ',	'<p>Thực hiện theo c&aacute;c bước nhỏ như h&igrave;nh ảnh m&ocirc; tả<br /><strong>Bước 1:&nbsp;</strong>Bấm v&agrave;o n&uacute;t <strong>Cập nhật v&agrave;o Carrotstore Music</strong><br /><strong>Bước 2:</strong>&nbsp;Bấm v&agrave;o n&uacute;t T<strong>h&ecirc;m v&agrave;o b&agrave;n l&agrave;m việc</strong> để b&aacute;o c&aacute;o c&ocirc;ng việc bạn vừa l&agrave;m v&agrave; để hệ thống chấm c&ocirc;ng cho bạn (c&aacute;c bước thực hiện b&aacute;o c&aacute;o bạn c&oacute; thể xem lại ở li&ecirc;n kết sau nếu qu&ecirc;n&nbsp;<a href=\"http://work.carrotstore.com/?page_show=help&amp;id=4\">http://work.carrotstore.com/?page_show=help&amp;id=4</a> )</p>',	35,	4,	0),
(39,	'Bước 1:nhập câu hỏi và câu trả lời',	'<p>Sau khi&nbsp; bấm v&agrave;o n&uacute;t +Chat&nbsp;sẽ xuất hiện một trang để bi&ecirc;n tập vi&ecirc;n nhập dữ liệu. Thực hiện c&aacute;c bước nhỏ như trong h&igrave;nh để ho&agrave;n tất việc th&ecirc;m c&acirc;u tr&ograve; chuyện.<br /><strong>Bước 1</strong>:Nhập c&acirc;u hỏi của người d&ugrave;ng, từ kh&oacute;a hỏi n&ecirc;n ngắn gọn kh&ocirc;ng d&agrave;i qu&aacute;</p><p><strong>Bước 2</strong>: Nhập c&acirc;u trả lời của nh&acirc;n vật, cần ch&uacute; &yacute; c&aacute;c điểm sau:<br />- Đ&uacute;ng ch&iacute;nh tả, ngữ ph&aacute;p,&nbsp;kh&ocirc;ng được viết tắt.<br />- Cố gắng khai th&aacute;c&nbsp;v&agrave; tưởng tượng ra những c&acirc;u trả lời th&uacute; vị . Thỉnh thoảng d&ugrave;ng từ kh&oacute;a&nbsp;<strong>{ten_user}</strong>&nbsp;để gọi t&ecirc;n người d&ugrave;ng v&agrave;o c&acirc;u&nbsp;trả lời (vd&nbsp;<a target=\"_blank\" href=\"http://carrotstore.com/app_my_girl_update.php?id=71022&amp;lang=vi\">như c&acirc;u tr&ograve; chuyện n&agrave;y - bấm v&agrave;o để xem</a>)<br />- Thỉnh thoảng&nbsp;th&ecirc;m những c&acirc;u tr&ograve; chuyện hỏi lại người d&ugrave;ng sau c&acirc;u trả lời&nbsp; để tăng t&iacute;nh tương t&aacute;c tr&ograve; chuyện liền mạch (vd&nbsp;<a target=\"_blank\" href=\"http://carrotstore.com/app_my_girl_update.php?id=70966&amp;lang=vi\">như c&acirc;u tr&ograve; chuyện n&agrave;y - bấm v&agrave;o để xem</a>) v&agrave; l&ocirc;i k&eacute;o người d&ugrave;ng đ&aacute;nh gi&aacute; ứng dụng, hoặc bấm v&agrave;o quảng c&aacute;o hoặc like fanpage (vd&nbsp;<a target=\"_blank\" href=\"http://carrotstore.com/app_my_girl_update.php?id=70401&amp;lang=vi\">như c&acirc;u tr&ograve; chuyện n&agrave;y - bấm v&agrave;o để xem</a>)<br />- Nếu người d&ugrave;ng nhờ mở hay bật tắt chức năng n&agrave;o đ&oacute; th&igrave; h&atilde;y chọn đ&uacute;ng chức năng&nbsp;<strong>(effect)</strong>&nbsp;li&ecirc;n quan khi nội dung c&acirc;u dạy c&oacute; đề cập tới (vd: &quot;mở đ&egrave;n pin gi&uacute;p t&ocirc;i&quot; n&ecirc;n đặt&nbsp;chức năng tắt đ&egrave;n pin &quot;Tắt đ&egrave;n pin&quot; , &quot;mở b&agrave;i h&aacute;t kh&aacute;c&quot; &quot;Mở nhạc kh&aacute;c&quot;. Nếu người d&ugrave;ng muốn nh&acirc;n vật cởi &aacute;o quần hoặc cho xem c&aacute;c bộ phận nhạy cảm tr&ecirc;n cơ thể th&igrave; chọn &quot;Mở shop trang phục (ver 2 - 3D) (app) &nbsp;nhằm tăng tỉ lệ mua h&agrave;ng trong ứng dụng&quot; v.v... ) nhớ k&egrave;m theo nội dung trả lời (chat) b&igrave;nh thường như người thật (vd&nbsp;<a target=\"_blank\" href=\"http://carrotstore.com/app_my_girl_update.php?id=61262&amp;lang=vi\">như c&acirc;u tr&ograve; chuyện n&agrave;y - bấm v&agrave;o để xem</a>)<br /><strong>Bước 3: C</strong>họn biểu tượng li&ecirc;n quan tới nội dung c&acirc;u trả lời.<br /><strong>Bước 4: C</strong>họn khu&ocirc;n mặt ph&ugrave; hợp với c&acirc;u trả lời.<br /><strong>Bước 5</strong>: Chọn h&agrave;nh động ph&ugrave; hợp với c&acirc;u trả lời.<br /><strong>Bước 6</strong>: Tạo giọng đọc v&agrave; tải l&ecirc;n&nbsp;nếu hệ thống y&ecirc;u cầu.&nbsp;Bấm v&agrave;o&nbsp;n&uacute;t &quot;Nghe thử&quot; để tạo giọng.<br /><strong>Bước 7:&nbsp;</strong>Chọn giới hạn mức độ th&ocirc; tục cho ph&ugrave; hợp với nội dung<br />mức 1: Nghi&ecirc;m ngặt - kh&ocirc;ng c&oacute; t&igrave;nh dục, ma t&uacute;y, thuốc, s&uacute;ng, bom, m&igrave;n, bạo lực, k&iacute;ch động người d&ugrave;ng.&nbsp;<br />mức 2: Cho ph&eacute;p t&igrave;nh dục th&ocirc; tục nhẹ.<br />mức 3: C&oacute; t&igrave;nh dục vừa phải, th&ocirc; tục mức độ vừa phải.<br />mức 4: C&oacute; t&igrave;nh dục nặng, th&ocirc; tục nặng.<br /><strong>Bước 8</strong>: Bấm n&uacute;t ho&agrave;n tất để ho&agrave;n tất việc nhập dữ liệu cho c&acirc;u tr&ograve; chuyện.</p>',	12,	4,	0),
(41,	'Báo cáo công việc khi đã nhập xong câu trò chuyện',	'<p>Khi tạo hoặc cập nhật xong c&acirc;u thoại, hệ thống sẽ tự động tạo ra một n&uacute;t &quot;Th&ecirc;m v&agrave;o b&agrave;n l&agrave;m việc&quot; để c&aacute;c bi&ecirc;n tập vi&ecirc;n bấm v&agrave;o khai b&aacute;o t&aacute;c vụ vừa l&agrave;m</p><p>Bạn c&oacute; thể xem th&ecirc;m chi tiết về việc khai b&aacute;o&nbsp;ở đường dẫn n&agrave;y:</p><p>&nbsp;<a href=\"http://work.carrotstore.com/?page_show=help&amp;id=4\">http://work.carrotstore.com/?page_show=help&amp;id=4</a></p>',	10,	4,	0),
(42,	'Chỉnh sửa thông tin âm nhạc',	'<p>Để chỉnh sửa th&ocirc;ng tin &acirc;m nhạc, c&aacute;c bi&ecirc;n tập vi&ecirc;n cần v&agrave;o phần danh s&aacute;ch lời b&agrave;i h&aacute;t, bấm v&agrave;o n&uacute;t như trong h&igrave;nh m&ocirc; tả ở mỗi quốc gia . C&ocirc;ng việc n&agrave;y thực hiện tương tự to&agrave;n bộ c&aacute;c nước c&oacute; trong danh s&aacute;ch.</p>',	2,	4,	0),
(43,	'Tìm kiếm đối tượng âm nhạc bị thiếu tham số',	'<p>Editor cần t&igrave;m kiếm c&aacute;c h&agrave;ng thiếu th&ocirc;ng tin để bổ sung. Bấm v&agrave;o n&uacute;t chỉnh sửa m&agrave;u v&agrave;ng như trong h&igrave;nh ảnh m&ocirc; tả. Khi ho&agrave;n tất một trang danh s&aacute;ch th&igrave; h&atilde;y chuyển sang trang kh&aacute;c bằng c&aacute;ch bấm v&agrave;o c&aacute;c con số hiển thị trang ở đầu trang</p>',	42,	4,	0),
(44,	'Bổ sung thông tin còn thiếu cho bài hát',	'<p>Trước khi nhập c&aacute;c trường dữ liệu c&ograve;n thiếu, Editor cầm bấm v&agrave;o n&uacute;t t&igrave;m kiếm để t&igrave;m kiếm c&aacute;c th&ocirc;ng tin về b&agrave;i h&aacute;t tr&ecirc;n google rồi nhập v&agrave;o c&aacute;c trường dữ liệu c&ograve;n thiếu như tr&ecirc;n h&igrave;nh</p><p>Những lưu &yacute; khi nhập dữ liệu :<br />- C&oacute; thể bỏ trống c&aacute;c trường khi kh&ocirc;ng t&igrave;m thấy th&ocirc;ng tin.<br />- Ở trường Năm (Year): chỉ nhập năm xuất bản (kh&ocirc;ng nhập ng&agrave;y ,th&aacute;ng). nếu kh&ocirc;ng t&igrave;m thấy năm ở trang ch&iacute;nh google th&igrave; bấm v&agrave;o c&aacute;c li&ecirc;n kết youtube để lấy năm theo năm m&agrave; video được xuất bản.<br />- Trường Thể loại (Genre):&nbsp;nhập v&agrave;o phải l&agrave; dữ liệu ng&ocirc;n ngữ tiếng anh hoặc tr&ugrave;ng với ng&ocirc;n ngữ lời b&agrave;i h&aacute;t (Editor n&ecirc;n đăng xuất t&agrave;i khoản google ra khỏi tr&igrave;nh duyệt để kh&ocirc;ng hiển thị tiếng Việt khi t&igrave;m kiếm th&ocirc;ng tin).</p><p>Sau khi nhập xong, bấm v&agrave;o n&uacute;t ho&agrave;n tất để kết th&uacute;c việc bổ sung dữ liệu.</p>',	42,	4,	0),
(45,	'báo cáo xong việc bổ sung thông tin cho bài hát',	'<p>Sau khi ho&agrave;n tất việc bổ sung sẽ c&oacute; một hộp thoại hiện ra c&ugrave;ng với n&uacute;t th&ecirc;m v&agrave;o b&agrave;n l&agrave;m việc.&nbsp;Editor bấm v&agrave;o để khai b&aacute;o việc vừa l&agrave;m.</p><p>c&aacute;c Edior c&oacute; thể xem lại việc b&aacute;o c&aacute;o tương tự như li&ecirc;n kết sau:</p><p>&nbsp;<a href=\"http://work.carrotstore.com/?page_show=help&amp;id=4\">http://work.carrotstore.com/?page_show=help&amp;id=4</a></p>',	42,	4,	0),
(46,	'Thêm ảnh nền',	'<p>Ở trang ch&iacute;nh CMS, bấm v&agrave;o mục quang cảnh ở thanh&nbsp;đầu trang để bắt đầu tải ảnh nền l&ecirc;n. Dựa v&agrave;o c&aacute;c chủ đề c&oacute; sẵn m&agrave; c&aacute;c bi&ecirc;n tập vi&ecirc;n c&oacute; thể t&igrave;m th&ecirc;m v&agrave;o bộ sưu tập quang cảnh của hệ thống.</p>',	2,	4,	0),
(47,	'chọn ảnh tải lên và nhập dữ liệu',	'<p>Những lưu &yacute; khi nhập dữ liệu ảnh nền:<br />- Đặt&nbsp;t&ecirc;n cho ảnh nền. Kh&ocirc;ng được để trống v&agrave; t&ecirc;n phải c&oacute; li&ecirc;n quan tới ảnh tải l&ecirc;n.<br />- Chọn tệp tải l&ecirc;n c&oacute; k&iacute;ch thước chất lượng kh&ocirc;ng qu&aacute; nhỏ, c&aacute;c editor c&oacute; thể xem qua hướng dẫn chọn v&agrave; tải ảnh ở li&ecirc;n kết sau</p><p>&nbsp;<a href=\"http://work.carrotstore.com/?page_show=help&amp;id=48\">http://work.carrotstore.com/?page_show=help&amp;id=48</a><br />- Chọn chủ đề (category) li&ecirc;n quan tới ảnh<br />- Nhập gi&aacute; bằng 0<br />- Id store nhập v&agrave;o l&agrave;&nbsp;com.product1.bk1<br />- Vesion : ver 1(2d)<br />Sau khi nhập xong c&aacute;c dữ liệu tr&ecirc;n th&igrave; h&atilde;y bấm v&agrave;o n&uacute;t &quot;Th&ecirc;m mới&quot; để hệ thống xuất hiện n&uacute;t &quot;Th&ecirc;m v&agrave;o b&agrave;n l&agrave;m việc&quot;. C&aacute;c bước b&aacute;o c&aacute;o như b&aacute;o c&aacute;o c&aacute;c c&ocirc;ng việc kh&aacute;c.&nbsp;&nbsp;Xem ở li&ecirc;n kết n&agrave;y nếu qu&ecirc;n&nbsp;</p><p>&nbsp;<a href=\"http://work.carrotstore.com/?page_show=help&amp;id=4\">http://work.carrotstore.com/?page_show=help&amp;id=4</a></p>',	46,	4,	0),
(48,	'Các chọn lựa ảnh đúng kích thước',	'<p>dựa v&agrave;o c&ocirc;ng cụ t&igrave;m kiếm ảnh&nbsp;<a href=\"https://www.google.com.vn/imghp?hl=en&amp;tab=ri&amp;authuser=0&amp;ogbl\">https://www.google.com.vn/imghp?hl=en&amp;tab=ri&amp;authuser=0&amp;ogbl</a>&nbsp;hoặc&nbsp;<a href=\"https://www.pinterest.com/\">https://www.pinterest.com/</a>&nbsp;c&aacute;c bi&ecirc;n tập vi&ecirc;n c&oacute; thể t&igrave;m kiếm c&aacute;c ảnh li&ecirc;n quan tới chủ đề muốn th&ecirc;m v&agrave;o bộ sưu tập quang cảnh.</p><p>Ch&uacute; &yacute; theo hướng dẫn để chọn ra những bức&nbsp;ảnh c&oacute; k&iacute;ch thước&nbsp;chiều cao tối đa, ảnh đưa v&agrave;o ứng dụng sẽ đẹp v&agrave; chất lượng, mang lại trải nghiệm tốt cho người d&ugrave;ng</p>',	46,	4,	0),
(49,	'Thêm nhạc',	'<p>Để th&ecirc;m nhạc v&agrave;o hệ thống c&aacute;c bi&ecirc;n tập vi&ecirc;n cần phải kiểm tra b&agrave;i h&aacute;t đ&atilde; c&oacute; hay chưa , nhằm đảm bảo t&iacute;nh kh&ocirc;ng dư thừa dữ&nbsp; liệu&nbsp;bằng c&ocirc;ng cụ <a href=\"http://carrotstore.com/app_my_girl_handling.php?func=check_music&amp;key_word=\"><strong>Kiểm tra tồn tại nhạc</strong></a> của&nbsp;CMS truy cập nhanh qua đường dẫn&nbsp;<a href=\"http://carrotstore.com/app_my_girl_handling.php?func=check_music&amp;key_word=\">http://carrotstore.com/app_my_girl_handling.php?func=check_music&amp;key_word=</a>&nbsp;hoặc v&agrave;o thanh tr&igrave;nh đơn đầu trang chọn v&agrave;o mục c&ocirc;ng cụ v&agrave; v&agrave;o mục&nbsp;<a href=\"http://carrotstore.com/app_my_girl_handling.php?func=check_music&amp;key_word=\"><strong>Kiểm tra tồn tại nhạc</strong></a></p>',	2,	2,	0),
(50,	'kiểm tra bài hát đã có trong danh sách',	'<p>C&aacute;c bi&ecirc;n tập vi&ecirc;n n&ecirc;n kiểm tra b&agrave;i h&aacute;t đ&atilde; c&oacute; hay chưa bằng c&ocirc;ng cụ&nbsp;<strong>Kiểm tra b&agrave;i h&aacute;t c&oacute; tồn tại</strong> ở li&ecirc;n kết&nbsp;<a href=\"http://carrotstore.com/app_my_girl_handling.php?func=check_music&amp;key_word=\">http://carrotstore.com/app_my_girl_handling.php?func=check_music&amp;key_word=</a></p><p>C&aacute;ch d&ugrave;ng:<br />Nhập từ kho&aacute; v&agrave;o thanh t&igrave;m kiếm v&agrave; bấm v&agrave;o n&uacute;t <strong>&quot;Kiểm tra lại từ kh&oacute;a&quot;.</strong><br />Hệ thống sẽ đưa ra c&aacute;c trạng th&aacute;i th&ocirc;ng b&aacute;o để bạn biết b&agrave;i&nbsp;h&aacute;t c&oacute; hay chưa để th&ecirc;m v&agrave;o, xem chi tiết c&aacute;c trạng th&aacute;i như trong h&igrave;nh để quyết định th&ecirc;m v&agrave;o danh s&aacute;ch hay kh&ocirc;ng</p><p>Cuối c&ugrave;ng bấm v&agrave;o c&aacute;c n&uacute;t th&ecirc;m nhanh b&agrave;i h&aacute;t b&ecirc;n dưới, t&ugrave;y v&agrave;o quốc gia m&agrave; bạn muốn th&ecirc;m b&agrave;i h&aacute;t &nbsp;theo đ&uacute;ng ng&ocirc;n ngữ b&agrave;i h&aacute;t</p>',	49,	4,	0),
(51,	'Nhập thông tin bài hát',	'<p>Thực hiện c&aacute;c bước như tr&ecirc;n h&igrave;nh hướng dẫn&nbsp;&nbsp;<br />Sao khi <strong>Th&ecirc;m mới</strong> b&agrave;i h&aacute;t th&agrave;nh c&ocirc;ng, hệ thống sẽ tạo ra một n&uacute;t th&ecirc;m v&agrave;o b&agrave;n l&agrave;m việc để c&aacute;c bi&ecirc;n tập vi&ecirc;n b&aacute;o c&aacute;o c&ocirc;ng việc của m&igrave;nh, c&aacute;c bước b&aacute;o c&aacute;o c&oacute; thể xem lại li&ecirc;n kết sau (nếu qu&ecirc;n)&nbsp;<a href=\"http://work.carrotstore.com/?page_show=help&amp;id=4\">http://work.carrotstore.com/?page_show=help&amp;id=4</a></p>',	49,	4,	0);
