function show_report_work(){$("#frm_add_report").show(200);$("#btn_add_report").hide(100)}function close_report_work(){$("#frm_add_report").hide(200);$("#btn_add_report").show(100)}function show_add_report_work(c,d,a,b){$("#report_type_chat").val(a);$("#report_lang_chat").val(d);$("#report_type_action").val(b);$("#report_id_chat").val(c);$("#report_func").val("add");$("#btn_add_and_edit_report").removeClass("orange");$("#btn_add_and_edit_report").addClass("green");show_report_work()}function submit_report_work(){var a=$("#frm_add_report").serializeArray();var b=$("#url_act").val();$.ajax({type:"post",url:b+"/ajax.php",data:a,success:function(c){$("#area_table_work").html(c);get_salary();close_report_work()},})}function edit_report_work(c){var b=$("#item_work_"+c).attr("data_report");var a=JSON.parse(b);$("#report_type_chat").val(a.type_chat);$("#report_lang_chat").val(a.lang_chat);$("#report_type_action").val(a.type_action);$("#report_id_chat").val(a.id_chat);$("#report_func").val("edit");$("#report_id").val(a.id);$("#btn_add_and_edit_report").removeClass("green");$("#btn_add_and_edit_report").addClass("orange");show_report_work()}function get_salary(){var a=$("#frm_get_salary").serializeArray();var b=$("#url_act").val();$.ajax({type:"post",url:b+"/ajax.php",data:a,success:function(c){$("#account_balance").html(c).effect("pulsate","slow").effect("highlight","slow")},})};